static uint64_t get_reaper_start_time(pid_t pid)
{
    int ret;
    FILE *f;
    uint64_t starttime;
    /* strlen("/proc/") = 6
     * +
     * LXCFS_NUMSTRLEN64
     * +
     * strlen("/stat") = 5
     * +
     * \0 = 1
     * */
#define __PROC_PID_STAT_LEN (6 + LXCFS_NUMSTRLEN64 + 5 + 1)
    char path[__PROC_PID_STAT_LEN];
    pid_t qpid;

    qpid = lookup_initpid_in_store(pid);
    if (qpid = __PROC_PID_STAT_LEN) {
        /* Caller can check for EINVAL on 0. */
        errno = EINVAL;
        return 0;
    }

    f = fopen(path, "r");
    if (!f) {
        /* Caller can check for EINVAL on 0. */
        errno = EINVAL;
        return 0;
    }

    /* Note that the *scanf() argument supression requires that length
     * modifiers such as "l" are omitted. Otherwise some compilers will yell
     * at us. It's like telling someone you're not married and then asking
     * if you can bring your wife to the party.
     */
    ret = fscanf(f, "%*d "      /* (1)  pid         %d   */
            "%*s "      /* (2)  comm        %s   */
            "%*c "      /* (3)  state       %c   */
            "%*d "      /* (4)  ppid        %d   */
            "%*d "      /* (5)  pgrp        %d   */
            "%*d "      /* (6)  session     %d   */
            "%*d "      /* (7)  tty_nr      %d   */
            "%*d "      /* (8)  tpgid       %d   */
            "%*u "      /* (9)  flags       %u   */
            "%*u "      /* (10) minflt      %lu  */
            "%*u "      /* (11) cminflt     %lu  */
            "%*u "      /* (12) majflt      %lu  */
            "%*u "      /* (13) cmajflt     %lu  */
            "%*u "      /* (14) utime       %lu  */
            "%*u "      /* (15) stime       %lu  */
            "%*d "      /* (16) cutime      %ld  */
            "%*d "      /* (17) cstime      %ld  */
            "%*d "      /* (18) priority    %ld  */
            "%*d "      /* (19) nice        %ld  */
            "%*d "      /* (20) num_threads %ld  */
            "%*d "      /* (21) itrealvalue %ld  */
            "%" PRIu64, /* (22) starttime   %llu */
             &starttime);
    if (ret != 1) {
        fclose(f);
        /* Caller can check for EINVAL on 0. */
        errno = EINVAL;
        return 0;
    }

    fclose(f);

    errno = 0;
    return starttime;
}

