// lxcfs/bindings.c: 2097
static bool recv_creds(int sock, struct ucred *cred, char *v)
{ struct msghdr msg = { 0 }; struct iovec iov; struct cmsghdr *cmsg; char cmsgbuf[CMSG_SPACE(sizeof(*cred))]; char buf[1]; int ret; int optval = 1; *v = '1'; cred->pid = -1; cred->uid = -1; cred->gid = -1; if (setsockopt(sock, SOL_SOCKET, SO_PASSCRED, &optval, sizeof(optval)) == -1) { lxcfs_error("Failed to set passcred: %s
", strerror(errno)); return false; } buf[0] = '1'; if (write(sock, buf, 1) != 1) { lxcfs_error("Failed to start write on scm fd: %s
", strerror(errno)); return false; } //准备用发送端同样规格的消息头 msg.msg_name = NULL; msg.msg_namelen = 0; msg.msg_control = cmsgbuf; msg.msg_controllen = sizeof(cmsgbuf); iov.iov_base = buf; iov.iov_len = sizeof(buf); msg.msg_iov = &iov; msg.msg_iovlen = 1; if (!wait_for_sock(sock, 2)) { lxcfs_error("Timed out waiting for scm_cred: %s
", strerror(errno)); return false; } //接收数据 ret = recvmsg(sock, &msg, MSG_DONTWAIT); if (ret < 0) { lxcfs_error("Failed to receive scm_cred: %s
", strerror(errno)); return false; } //读取额外的控制信息 cmsg = CMSG_FIRSTHDR(&msg); if (cmsg && cmsg->cmsg_len == CMSG_LEN(sizeof(struct ucred)) && cmsg->cmsg_level == SOL_SOCKET && cmsg->cmsg_type == SCM_CREDENTIALS) { memcpy(cred, CMSG_DATA(cmsg), sizeof(*cred)); } //buf中已经写入发送过来的数据 *v = buf[0]; return true;
}

